import React, { useState, useRef } from 'react';
import './App.css';

function App() {
  const [joke, setJoke] = useState('');
  const [showEmoji, setShowEmoji] = useState(false);

  const liveRegionRef = useRef(null);

  const fetchJoke = async () => {
    try {
      setShowEmoji(true);
      const response = await fetch('http://localhost:8080/get-joke');
      if (!response.ok) {
        throw new Error('Failed to fetch joke');
      }
      const data = await response.json();
      setJoke(data.joke);
      announceJoke(data.joke); // Announce the joke for screen readers
    } catch (error) {
      console.error('Error fetching joke:', error);
    } finally {
      setTimeout(() => setShowEmoji(false), 2000);
    }
  };

  const announceJoke = (text) => {
    if (liveRegionRef.current) {
      liveRegionRef.current.textContent = text;
    }
  };

  return (
    <div className="App">
      <h1>Dad Joke Generator</h1>
      <button onClick={fetchJoke}>Get a Joke</button>
      {joke && <p>{joke}</p>}
      <div aria-live="polite" style={{ position: 'absolute', clip: 'rect(1px, 1px, 1px, 1px)', width: '1px', height: '1px', overflow: 'hidden' }} ref={liveRegionRef}></div>
      <div>{showEmoji && <img className="emoji" alt="dad laughing" src="laughing-dad.png" />}</div>
    </div>
  );
}

export default App;
