import React, { useState, useEffect, useRef } from 'react';
import './App.css';

function App() {
  const [joke, setJoke] = useState('');
  const [showEmoji, setShowEmoji] = useState(false);
  const [announcement, setAnnouncement] = useState('');

  const liveRegionRef = useRef(null);

  const fetchJoke = async () => {
    try {
      setShowEmoji(true);
      const response = await fetch('http://localhost:8080/get-joke');
      if (!response.ok) {
        throw new Error('Failed to fetch joke');
      }
      const data = await response.json();
      setJoke(data.joke);
      setAnnouncement(data.joke); // Set the announcement for live region
    } catch (error) {
      console.error('Error fetching joke:', error);
    } finally {
      setTimeout(() => setShowEmoji(false), 2000);
    }
  };

  useEffect(() => {
    if (announcement && liveRegionRef.current) {
      liveRegionRef.current.textContent = announcement; // Update live region content
      setAnnouncement(''); // Clear announcement after update
    }
  }, [announcement]);

  return (
    <div className="App">
      <h1>Dad Joke Generator</h1>
      <button onClick={fetchJoke}>Get a Joke</button>
      {joke && <p>{joke}</p>}
      <div aria-live="polite" ref={liveRegionRef}></div>
      <div>{showEmoji && <img className="emoji" alt="dad laughing" src="laughing-dad.png" />}</div>
    </div>
  );
}

export default App;
